INSERT INTO `wp_postmeta` VALUES (1,2,'_wp_page_template','default'),(2,3,'_wp_page_template','default'),(7,8,'_form','<label> Your Name (required)\n    [text* your-name] </label>\n\n<label> Your Email (required)\n    [email* your-email] </label>\n\n<label> Subject\n    [text your-subject] </label>\n\n<label> Your Message\n    [textarea your-message] </label>\n\n[submit \"Send\"]'),(8,8,'_mail','a:8:{s:7:\"subject\";s:20:\"OBE \"[your-subject]\"\";s:6:\"sender\";s:42:\"[your-name] <wordpress@obe.preparatio.com>\";s:4:\"body\";s:217:\"From: [your-name] <[your-email]>\nSubject: [your-subject]\n\nMessage Body:\n[your-message]\n\n-- \nThis e-mail was sent from a contact form on OBE (http://development-webapplication-alb-755677791.us-east-2.elb.amazonaws.com)\";s:9:\"recipient\";s:21:\"aivanov@aximcloud.com\";s:18:\"additional_headers\";s:22:\"Reply-To: [your-email]\";s:11:\"attachments\";s:0:\"\";s:8:\"use_html\";i:0;s:13:\"exclude_blank\";i:0;}'),(9,8,'_mail_2','a:9:{s:6:\"active\";b:0;s:7:\"subject\";s:20:\"OBE \"[your-subject]\"\";s:6:\"sender\";s:34:\"OBE <wordpress@obe.preparatio.com>\";s:4:\"body\";s:159:\"Message Body:\n[your-message]\n\n-- \nThis e-mail was sent from a contact form on OBE (http://development-webapplication-alb-755677791.us-east-2.elb.amazonaws.com)\";s:9:\"recipient\";s:12:\"[your-email]\";s:18:\"additional_headers\";s:31:\"Reply-To: aivanov@aximcloud.com\";s:11:\"attachments\";s:0:\"\";s:8:\"use_html\";i:0;s:13:\"exclude_blank\";i:0;}'),(10,8,'_messages','a:8:{s:12:\"mail_sent_ok\";s:45:\"Thank you for your message. It has been sent.\";s:12:\"mail_sent_ng\";s:71:\"There was an error trying to send your message. Please try again later.\";s:16:\"validation_error\";s:61:\"One or more fields have an error. Please check and try again.\";s:4:\"spam\";s:71:\"There was an error trying to send your message. Please try again later.\";s:12:\"accept_terms\";s:69:\"You must accept the terms and conditions before sending your message.\";s:16:\"invalid_requir